var mysql=require('mysql');
 
 function connectServer(){
 
     var client=mysql.createConnection({
         host:'localhost',
         user:'root',
         password:'rootroot',
         database:'mysql'
     })
 
     return client;
 }
 //查找商品列表
  function  selectShop(client,callback){
     //client为一个mysql连接对象
     client.query('select * from imgList',function(err,results){
         if(err) throw err;
// 			console.log(results)
         callback(results);
     });
 }
 function  selectFun(client,username,callback){
     //client为一个mysql连接对象
     client.query('select password from userZhou where username="'+username+'"',function(err,results,fields){
         if(err) throw err;
 			console.log(err)
         callback(results);
     });
 }
 
 function insertFun(client , username , password,callback){
     client.query("insert into userZhou(username,password) values('"+username+"','"+password+"')", [username, password], function(err,result){
         if( err ){
             console.log( "error:" + err.message);
             return err;
         }
           callback(err);
     });
 }
 
 exports.connect = connectServer;
 exports.selectFun  = selectFun;
 exports.insertFun = insertFun;
